import { response } from 'express'
import React, { Component } from 'react'
import {Link} from 'react-router-dom'

export default class login extends Component {
  state = {
        email : '',
        password: '',
        user:''
  }
  handleChange = (e)=>{
    this.setState({
      [e.target.name]: e.target.value
    })
    console.log('state', this.state);
  }

  handleSubmit = async (e)=>{
    e.preventDefault()
    if(this.state.user === 'employer'){
      await fetch(`localhost:3001/user/employerlogin`,{
        method: 'POST',
        headers:{'Content-Type': 'application/json'},
        body: JSON.stringify(this.state)
      })
      .then(response => response.json())
      .then(data => {
        console.log('token', data);
        if(data.token !== null){
            localStorage.setItem('key', data.token);
        }
        const currentRoute = this.props.history.location.pathname;
        if(currentRoute === '/'){
          this.props.history.push('employerHome')
        }else{this.props.history.push(currentRoute)}
      }).catch(e => console.log(e))
    }
  }
  render() {
    return (
      <div className = "loginFormWrapper">
        <div className = 'loginForm'>
        <div><h3>Log in and get to work </h3></div> 
        <div className = "formContainer">
          <form onSubmit= {this.handleSubmit}>
          <input type = 'email' name = 'email' className = 'form-control loginInput' placeholder = 'Enter your email' onChange = {this.handleChange}/><br/>
          <input type = 'password' name = 'password' className = 'form-control loginInput' placeholder = 'Enter your password' onChange = {this.handleChange}/><br/>
          <select name="user" className="form-control loginIput" onChange={this.handleChange}>
						<option>Select type of user</option>
						<option value="employer">Employer</option>
						<option value="worker">Worker</option>
					</select>
          <div>
          <span><input type = "checkbox"/> Keep me logged in</span>
          <span className = "forgetpassword"><Link>Forget Password?</Link></span>
          </div>
          <button className="continueButton" on>Continue</button>
          </form>
          </div>
        <div>
          <h5>New to Findjob?</h5>
          <Link to = '/signup'>Sign Up</Link>
        </div>
        </div>
      </div>
    )
  }
}
