import React, { Component } from 'react'
import { Link } from 'react-router-dom'

export default class JobseekerHome extends Component {
  state = {
        jobsInformation: {}
  };

  componentDidMount = async () =>{
    await fetch('https://3001/jobs/getjobs', {
      method : 'GET',
      headers: {'Content-type': 'application/json'}
    }).then(response => response.json())
    .then(data => {
      if(data){
        
      }
    })
    .catch(err => console.log(err));
    }

    mounting = () => {
      this.setState({
        jobsInformation: Object.assign()
      })
    }
  render() {
    return (
      <div className = 'jobSeekerContainer'>
        <div className = 'jobseekerHeader'>
          <div className = 'logo'>LOGO</div>
          <div className = 'jobseekerNav'>
            <ul className = 'jobseekerNavItemsWrapper'>
              <li className  ='jobseekerNavItems'><Link to = '' className ='jobseekerNavItemsLink' >Profile</Link></li>
              <li className = 'jobseekerNavItems'><Link to ='' className ='jobseekerNavItemsLink'>Jobs</Link></li>
              <li className = 'jobseekerNavItems'><Link to ='' className ='jobseekerNavItemsLink'>Logout</Link></li>
            </ul>
          </div> 
          </div>  
         <div className = 'jobseekerMain'>
           <div className = 'jobsCard'>
             <h3 className = 'jobsTitle'> Software Engineer</h3>
             {this.state.map ((item) => {
               console.log('item', item);
             <div>
               <ul className = 'jobsCardContentWrapper'>
                 <li className = 'jobsCardContent'>Company: <span>Dobas Inc</span></li>
                 <li className = 'jobsCardContent'>Industry : <span>{item.industry}</span></li>
                 <li className = 'jobsCardContent'>Experience level : <span>Intermediate</span></li>
                 <li className = 'jobsCardContent'>Location : <span>Lagos</span></li>
                 <li className = 'jobsCardContent'>work Type : <span>Full Time </span></li>
                 <li className = 'jobsCardContent'>Job Description : <span>You'll create, maintain, audit and improve systems to meet particular needs, often as advised by a systems analyst or architect, testing both hard and software systems to diagnose and resolve system faults. ... The work of a software engineer can also form part of a multimedia programmer role</span></li>
                  <li className = 'submitBidButton'> Submit Bid</li>
               </ul>
             </div>
             })}
           </div>
           </div>       
      </div>
    )
  }
}
