import React, { Component } from 'react'

export default class PostJobs extends Component {
  state = {
    industry: '',
    location: '',
    qualification: '',
    experience: '',
    job_level: '',
    job_summary: '',
    job_description: '',
    job_title: '',
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    })
  }

  handleSubmit = async (e) => {
    e.preventDefault();
    await fetch('http://localhost/3001/jobs/addjobs',{
      method: 'POST',
      headers: {'Content-Type': 'application/json' },
      body: JSON.stringify(this.state)
    })
    .then(response => response.json())
    .then(jsonResponse => {
      if(jsonResponse.message === 'created successfully'){
        alert({message: 'You have posted a job successfully'})
      }else {
        alert({message: 'Failed to post a job'});
      }
    })
    .catch(err => console.log(err))
    this.setState({
      [e.target.name]: ''
    })
  }
  render() {
    const {handleSubmit, handleChange} = this
    return (
      <div className = 'postJobWrapper'>
        <form>
          <input type ='text' name = 'industry' className = 'form-control' placeholder = 'enter industry e.g IT' onChange = {handleChange}/><br/>
          <input type ='text' name = 'location' className = 'form-control' placeholder = 'enter location of job' onChange = {handleChange}/><br/>
          <input type ='text' name = 'qualification' className = 'form-control' placeholder = 'enter quaalification required' onChange = {handleChange}/><br/>
          <input type = 'text' name = 'experience' className = 'form-control' placeholder = 'enter years of experience required' onChange = {handleChange}/><br/>
          <input type = 'text' name = 'job_level' className = 'form-control' placeholder = 'full-time/part-time' onChange = {handleChange}/><br/>
          <input type = 'text' name = 'job_summary' className = 'form-control' placeholder = 'enter job summary' onChange = {handleChange}/><br/>
          <input type = 'text' name = 'job_description' className = 'form-control' placeholder = 'enter job description' onChange = {handleChange}/><br/>
          <input type = 'text' name = 'job_title' className = 'form-control' placeholder = 'enter title of job e.g front-end developer' onChange = {handleChange}/><br/>
          <button onClick = {handleSubmit}>Post</button>
          </form>
      </div>
    )
  }
}
