import React, { Component } from 'react'
import {Link} from 'react-router-dom'

export default class login extends Component {
  state = {
        email : '',
        password: ''
  }
  handleChange = (e)=>{
    this.setState({
      [e.target.name]: e.target.value
    })
    console.log('state', this.state);
  }

  handleSubmit = (e)=>{
    e.preventDefault()
  }
  render() {
    return (
      <div className = "loginFormWrapper">
        <div className = 'loginForm'>
        <div><h3>Log in and get to work </h3></div> 
          <form onSubmit= {this.handleSubmit}>
          <input type = 'email' name = 'email' className = 'form-control' placeholder = 'Enter your email' onChange = {this.handleChange}/><br/>
          <input type = 'password' name = 'password' className = 'form-control' placeholder = 'Enter your password' onChange = {this.handleChange}/><br/>
          <div>
          <span ></span>
          </div>
          <button className="continueButton">Continue</button>
          </form>
        <div>
          <h5>New to Findjob?</h5>
          <Link to = '/signup'>Sign Up</Link>
        </div>
        </div>
      </div>
    )
  }
}
