import React, { Component } from 'react'
import JobseekerBiodata from './jobseekerBiodata'
import JobseekerProfile from './jobseekerProfile'

export default class JobseekersignUpContainer extends Component {
  state = {
    step:1,
    
  }
  
  // process to next step 
  next = () => {
    this.setState(prevState =>{
      return {step: prevState.step + 1}
    })
  }
  //process to back 
  back = () => {
    this.setState(prevState => {
      return {step:prevState.step - 1}
    })
  }
  // handle all the field changes
  handlChange = (e)=>{
      this.setState({
        [e.target.name] : e.target.value
      })
      console.log('state',this.state);
  }
  // handle form submit 

  handleSubmit = () => {
        this.setState(
          {signupSuccess: true}
        )
        // console.log('state', this.state)
  }
  render() {
    // const {email,
    //   firstname,
    //   lastname,
    //   password,
    //   school,
    //   date,
    //   degree,
    //   discipline,
    //   skills,
    //   work_history,
    //   certification,
    //   other_experience,} = this.state;
    // const values = {email,
    // firstname,
    // lastname,
    // password,
    // school,
    // date,
    // degree,
    // discipline,
    // skills,
    // work_history,
    // certification,
    // other_experience,}
    // console.log('state', this.state)
    if(this.state.step === 1){
      return <JobseekerBiodata 
        next={this.next}
        handlChange = {this.handlChange}
        // values = {values}
      />
    }
    else if(this.state.step === 2){
      return <JobseekerProfile
      back = {this.back}
      handlChange = {this.handlChange}
      handleSubmit = {this.handleSubmit}
      // values = {values}
      />
    }
    return (
      <div>
        
      </div>
    )
  }
}


const mapStateToProps = (state) => {
  const userReducer = state
  signupFormDetails: userReducer.signupFormDetails
}
